{
  "title": "Urgent Shipment Update Request",
  "description": "Joseph Damasio (platinijcd) is requesting an update on container shipment with ID #0987743. He needs an immediate status report on the container's progress."
}

Sender Name:{{1.from.name}}
Sender Email: {{1.from.address}}
Email subject: {{1.subject}}
Email body: {{1.text}}

mapper.blocks:
{
	"blocks": [
		{
			"type": "section",
			"text": {
				"type": "plain_text",
				"text": ":email: You have a new email:",
				"emoji": true
			}
		},
		{
			"type": "divider"
		},
		{
			"type": "section",
			"text": {
				"type": "mrkdwn",
				"text": "*{{70.title}}*"
			}
		},
		{
			"type": "section",
			"text": {
				"type": "mrkdwn",
				"text": "{{70.description}}"
			}
		},
		{
			"type": "section",
			"fields": [
				{
					"type": "mrkdwn",
					"text": "*Date:*\n{{formatDate(1.date; "YYYY-MM-DD hh:mm")}}"
				},
				{
					"type": "mrkdwn",
					"text": "*From:*\n{{1.from.name}}  //  {{1.from.address}}"
				}
			]
		},
		{
			"type": "actions",
			"elements": [
				{
					"type": "button",
					"text": {
						"type": "plain_text",
						"emoji": true,
						"text": "Open the Mail"
					},
					"style": "primary",
					"value": "click_me_123",
					"url": "{{1.messageLink}}",
					"action_id": "button-action"
				}
			]
		}
	]
}

